const CLITable = require('cli-table');

const TABLE_HEAD = {
    head: ['Contract', 'Action', 'Transaction ID',]
}

class ReportTable {
    constructor() {
        this.table = new CLITable(TABLE_HEAD);
    }

    addRow(rowData) {
        this.table.push(rowData);
    }

    draw() {
        console.log(this.table.toString());
    }
}

module.exports = new ReportTable();
