const chalk = require('chalk').default;
const CLITable = require('cli-table');

const TABLE_HEAD = {
    head: ['', 'Contract', 'Action', 'CPU ( MIN / MAX )', 'NET', 'RAM', 'Calls']
}

class ReportTable {
    constructor() {
        this.table = new CLITable(TABLE_HEAD);
    }

    addRow(rowData) {
        rowData[3] = `${rowData[4][0]} μs | ${rowData[4][1]} μs`;
        rowData[4] = `${rowData[5][0]} Bytes | ${rowData[5][1]} Bytes`;
        rowData[5] = `${rowData[6][0]} Bytes ${rowData[6][1]} Bytes`;
        this.table.push(rowData);
    }

    addSection(sectionName, sectionRows) {
        this.table.push({ [chalk.cyanBright(sectionName)]: ['', '', '', '', '', ''] });

        for (let i = 0; i < sectionRows.length; i++) {
            const sectionRow = sectionRows[i];
            sectionRow.unshift('');
            this.addRow(sectionRow);
        }
    }

    draw() {
        console.log(this.table.toString());
    }
}

module.exports = ReportTable;
