const nonceOption = async function(optionals, rawTransaction) {
    if (optionals && optionals.nonce) {
        rawTransaction.actions.push({
            account: "eosio.null",
            name: "nonce",
            authorization: rawTransaction.defaultExecutor,
            data: {
                nonce: Date.now()
            }
        });

        // Todo: Form a eosio.null action in order to call for nonce
    }
};

