import QueryProp from '../query-prop';
import { attach } from '../helpers/attach-queries';

class FromQuery extends QueryProp {

    public props = { 'code': '', 'scope': '', default: true }

    public from(contractName: string): any {
        if (!contractName) {
            throw new Error('You should provide from argument')
        }

        this.props.code = contractName;
        this.props.scope = contractName;
        this.props.default = false

        const queueQueries = attach({}, this.queries)
        return queueQueries
    }
}

export default FromQuery

