import QueryProp from '../query-prop';
import { attach } from '../helpers/attach-queries';

class LimitQuery extends QueryProp {

    // This is the default limit
    public props = { 'limit': 1, default: true }

    public limit(limit: number): any {
        this.props.limit = limit
        this.props.default = false

        const queueQueries = attach({}, this.queries)
        return queueQueries
    }
}

export default LimitQuery

