import QueryProp from '../query-prop';
import { attach } from '../helpers/attach-queries';

class RangeQueryQuery extends QueryProp {

    public props = { 'lower_bound': '', 'upper_bound': '', default: true }

    public range(minValue: any, maxValue: any): any {
        this.props.lower_bound = minValue
        this.props.upper_bound = maxValue
        this.props.default = false

        const queueQueries = attach({}, this.queries)
        return queueQueries
    }
}


export default RangeQueryQuery

