module.exports = function (data) {
    return {
        instanceOf: function (ofObject) {
            if (data == null || data == undefined) {
                throw new Error(`Not an instance of ${ofObject}`);
            }

            recursivelyCheckIfInstance(data, ofObject);
        }
    }
}

