class ProxyHandler {
    bam() {
        console.log('You are very clever')
    }
}
const b = new ProxyHandler();

const proxyHandler = {
    get: (obj, value) => {
        if (!obj.hasOwnProperty(value)) {
            console.log(value)
        }
    }
}

const a = new Proxy((value) => {
    console.log(value)
}, proxyHandler);

console.log(a.bam)