class ProxyHandler {
    bam() {
        console.log('You are very clever')
    }
}
const b = new ProxyHandler();

const proxyHandler = {
    construct(target, args) {
        return (value) => {
            console.log(value)
        }
    },
    get: (obj, value) => {
        if (!obj.hasOwnProperty(value)) {
            console.log(value)
            return true
        }
    }
}

const a = new Proxy({}, proxyHandler);

a.bam